def scroll_cb(event):
	curr = event.inaxes.get_xlim()
	mid = (curr[0] + curr[1])/2
	diff = (curr[1] - curr[0])/2
	if event.button == 'up':
		curr = (mid - diff/2, mid + diff/2)
	if event.button == 'down':
		curr = (mid - diff*2, mid + diff*2)
	event.inaxes.set_xlim(curr)
	event.canvas.draw()


def key_press_cb(event):
	curr = event.inaxes.get_xlim()
	diff = (curr[1] - curr[0]) / 2
	if event.key == 'right':
		curr = (curr[0] + diff/3, curr[1] + diff/3)
	elif event.key == 'left':
		curr = (curr[0] - diff/3, curr[1] - diff/3)
	event.inaxes.set_xlim(curr)
	event.canvas.draw()


x_last, y_last = 0, 0

def mouse_move_cb(event):
	global x_last, y_last
	print(event.inaxes.figure.get_figwidth())
	if event.button == 1:
		x, y = event.xy
		if x_last + y_last != 0:
			dx = x - x_last
		x_last, y_last = x, y


def update_waveform(id=0, y=None):
	if y is None or len(y) < 1:
		return
	axs[id].cla()
	axs[id].step(range(len(y)), y, where='mid', marker='o')
	axs[id].grid(axis='x', color='green', linestyle='--', linewidth=0.5)


def plot_test(y=None):
	fig = plt.figure()
	gs = fig.add_gridspec(2, hspace=0)
	axs = gs.subplots(sharex=True, sharey=True)

	plt.xticks(range(len(y)))
	plt.yticks([0, 1])

	axs[0].step(range(len(y)), y, where='mid', marker='o')
	axs[1].step(range(len(y)), y[::-1], where='mid', marker='o')

	axs[0].tick_params(top=True, labeltop=True, bottom=False, labelbottom=False)
	axs[-1].tick_params(top=False, labeltop=False, bottom=False, labelbottom=False)
	for ax in axs:
		ax.label_outer()
		ax.spines['top'].set_visible(False)
		ax.spines['bottom'].set_visible(False)
		ax.grid(axis='x', color='green', linestyle='--', linewidth=0.5)

	axs[0].spines['top'].set_visible(True)
	axs[-1].spines['bottom'].set_visible(True)

	plt.show()


axs = []

def gui(y=None):
	global axs
	backend_bases.NavigationToolbar2.toolitems = ()

	root = tkinter.Tk()
	root.wm_title("Embedded in Tk")

	fig = Figure(figsize=(5, 4), dpi=100)
	gs = fig.add_gridspec(2, hspace=0)
	axs = gs.subplots(sharex=True, sharey=True)

	plt.xticks(range(len(y)))
	plt.yticks([0, 1])

	axs[0].step(range(len(y)), y, where='mid', marker='o')
	axs[1].step(range(len(y)), y[::-1], where='mid', marker='o')

	axs[0].tick_params(top=True, labeltop=True, bottom=False, labelbottom=False)
	axs[-1].tick_params(top=False, labeltop=False, bottom=False, labelbottom=False)
	for ax in axs:
		ax.set_yticks([0, 1])
		ax.set_ylim([-0.25, 1.25])
		ax.label_outer()
		ax.spines['top'].set_visible(False)
		ax.spines['bottom'].set_visible(False)
		ax.grid(axis='x', color='green', linestyle='--', linewidth=0.5)

	axs[0].spines['top'].set_visible(True)
	axs[-1].spines['bottom'].set_visible(True)


	canvas = FigureCanvasTkAgg(fig, master=root)  # A tk.DrawingArea.
	canvas.draw()

	# pack_toolbar=False will make it easier to use a layout manager later on.
	toolbar = NavigationToolbar2Tk(canvas, root, pack_toolbar=False)
	toolbar.update()

	canvas.mpl_connect("key_press_event", key_press_cb)
	canvas.mpl_connect("scroll_event", scroll_cb)
	#canvas.mpl_connect("motion_notify_event", mouse_move_cb)

	button_quit = tkinter.Button(master=root, text="Quit", command=root.destroy)

	button_quit.pack(side=tkinter.BOTTOM)
	toolbar.pack(side=tkinter.BOTTOM, fill=tkinter.X)
	canvas.get_tk_widget().pack(side=tkinter.TOP, fill=tkinter.BOTH, expand=True)

	tkinter.mainloop()


